export default class Sol {
    constructor(x, y, image, c) {
        this.position = {
            x,
            y
        }
        this.c = c
        this.image = image
        this.width = image.width
        this.height = image.height
        this.degree = 0

    }

    draw() {
        this.c.drawImage(this.image, this.position.x, this.position.y)


    }
    update(IS_RUNNING) {
        if (IS_RUNNING) {
            console.log('rotate', this.degree)
            this.c.rotate(this.degrees * Math.PI / 180);
            this.degree++;
            this.draw();
        }
    }
}
