import QrcodeVue from "qrcode.vue";
import { gsap } from "gsap";
import { Draggable } from "gsap/Draggable";
gsap.registerPlugin(Draggable);
export default {
  name: "HomeView",
  data() {
    return {
      size: 300,
      serial: 0
    };
  },
  computed: {
    data: {
      get() {
        return this.$store.getters.info[this.serial];
      },
      set(newSerial) {
        return this.$store.getters.info[newSerial];
      }
    },
    value() {
      return this.data.url;
    }
  },
  methods: {
    changeSerial() {
      this.serial = Math.floor(Math.random() * this.$store.getters.info.length);
    }
  },
  watch: {
    data: function (newData) {
      console.log("changing", newData);
    },
    serial: function (newSerial) {
      console.log("change", newSerial);
      gsap.from(".title", {
        delay: 0.5,
        duration: 2.5,
        autoAlpha: 0,
        ease: "elastic.out(1, 0.3)",
        x: -200
      });
      gsap.from(".cos", {
        delay: 3,
        duration: 2.5,
        autoAlpha: 0,
        ease: "back.out(1, 0.3)",
        x: +400
      });
      gsap.from(".intro", {
        delay: 1,
        duration: 2.5,
        x: -400,
        autoAlpha: 0,
        rotation: 360
      });
      Draggable.create(".img", {
        bounds: "body"
      });
      this.data(newSerial);
    }
  },
  components: {
    QrcodeVue
  }
};
