import "core-js/modules/es.array.push.js";
export default {
  name: "ImagesView",
  data() {
    return {
      selectedDay: null,
      hover: false,
      currentImage: null
    };
  },
  computed: {
    days() {
      console.log(this.$store.getters.days);
      return this.$store.getters.days;
    },
    images() {
      console.log(this.$store.getters.images);
      return this.$store.getters.images.find(d => d._id == this.$route.params.id);
    }
  },
  methods: {
    getDay(id) {
      this.selectedDay = id;
    },
    goToImage() {
      this.currentImage = this.$store.getters.images.map(d => {
        return d._id;
      }).indexOf(this.$route.params.id);
      console.log('curr img', this.currentImage);
      this.$router.push({
        path: '/image/' + this.$store.getters.images[this.currentImage + 1]._id
      });
    },
    goToPrevImage() {
      this.currentImage = this.$store.getters.images.map(d => {
        return d._id;
      }).indexOf(this.$route.params.id);
      console.log('curr img', this.currentImage);
      this.$router.push({
        path: '/image/' + this.$store.getters.images[this.currentImage - 1]._id
      });
    }
  }
};
