export default {
  name: "NotebookView",
  data() {
    return {
      selectedDay: null
    };
  },
  beforeMount() {
    if (localStorage.getItem('savedData')) {
      this.saved = JSON.parse(localStorage.getItem('savedData'));
    }
  },
  computed: {
    notebook() {
      console.log(this.$store.getters.notebook);
      if (this.selectedDay != null) {
        return this.$store.getters.notebook.filter(n => n.map == this.selectedDay);
      } else {
        return this.$store.getters.notebook;
      }
    },
    days() {
      console.log(this.$store.getters.days);
      return this.$store.getters.days;
    }
  },
  methods: {
    saveCard(text) {
      if (localStorage.getItem('savedData')) {
        this.saved = JSON.parse(localStorage.getItem('savedData'));
      }
      if (this.saved.text.includes(text._id)) {
        console.log(this.saved.text.indexOf(text._id));
        this.saved.text.splice(this.saved.text.indexOf(text._id), 1);
      } else {
        this.saved.text.push(text._id);
      }
      localStorage.setItem('savedData', JSON.stringify(this.saved));
    },
    changeId() {
      this.id = this.days[this.dia]._id;
      this.dia++;
    }
  }
};
