export default class Player {
    constructor(image, c, canvas) {
        this.position = {
            x: 100,
            y: 280
        }
        this.velocity = {
            x: 0,
            y: 0
        }
        this.width = 199.7
        this.height = 400
        this.c = c
        this.canvas = canvas
        this.image = image
        this.frames = 1
        this.curr_frame = 0;
        this.speed = 7;

    }
    draw() {
        this.c.drawImage(
            this.image,
            199.7 * this.frames,
            0,
            199.7,
            400,
            this.position.x,
            this.position.y,
            this.width,
            this.height)
    }

    update(IS_RUNNING) {
        console.log(this.curr_frame, this.speed)
        if (this.curr_frame > this.speed) {
            this.curr_frame = 0;
            if (IS_RUNNING == true) {
                this.frames++
                if (this.frames > 9) this.frames = 1
            } else {
                this.frames = 0;
            }
        }
        if (this.position.x >= this.canvas) {
            this.frames = 0;
        }
        else {
            this.curr_frame++;
        }
        if (this.x > this.canvas.width - this.width) {
            this.speed = 0;
        }
        this.draw()
        this.position.x += this.velocity.x
    }
}
