import "core-js/modules/es.array.push.js";
// @ is an alias to /src
//import sprite from './../img/sprite_1.png'
import b00 from '..//../img/b0/mountain.png';
import b01 from '../img/b0/mountain2.png';
import b02 from '../img/b0/mountain3.png';
import b10 from '../img/b1/cami.png';
import b11 from '../img/b1/conreu.png';
import b12 from '../img/b1/conreu2.png';
import b20 from '../img/b2/panta.png';
import b21 from '../img/b2/panta2.png';
import b22 from '../img/b2/panta3.png';
import b23 from '../img/b2/terra.png';
import b0 from '../img/b/background.png';
import b1 from '../img/b/nuvol.png';
import b2 from '../img/b/sol.png';
import Player from './../classes/player.js';
import Platform from './../classes/platform.js';
export default {
  name: "DayView",
  props: {
    msg: String
  },
  computed: {
    day() {
      console.log(this.$store.getters.days);
      return this.$store.getters.days.find(d => d._id == this.msg);
    }
  },
  data() {
    return {
      id: '63b3e1b9f227fb373558a9a6',
      dia: 0,
      click: false,
      player: null,
      platform: null,
      canvas: null,
      generalBackgrounds: null,
      customBackgrounds: null,
      gravity: null,
      IS_RUNNING: false,
      c: null,
      keys: {
        right: {
          pressed: false
        },
        left: {
          pressed: false
        }
      }
    };
  },
  mounted() {
    this.canvas = this.$refs.canvas;
    this.c = this.canvas.getContext('2d');
    this.generalBackgrounds = [b0, b1, b2];
    this.customBackgrounds = [[{
      x: -50,
      y: 150,
      image: this.createImage(b00)
    }, {
      x: -40,
      y: 350,
      image: this.createImage(b01)
    }, {
      x: -90,
      y: 400,
      image: this.createImage(b02)
    }, {
      x: -30,
      y: 600,
      image: this.createImage(b10)
    }], [{
      x: -1000,
      y: 150,
      image: this.createImage(b12)
    }, {
      x: -1500,
      y: 350,
      image: this.createImage(b11)
    }, {
      x: -10,
      y: 600,
      image: this.createImage(b23)
    }], [{
      x: -10,
      y: 150,
      image: this.createImage(b22)
    }, {
      x: 0,
      y: 300,
      image: this.createImage(b21)
    }, {
      x: -10,
      y: 250,
      image: this.createImage(b20)
    }, {
      x: -10,
      y: 600,
      image: this.createImage(b23)
    }]];
    this.canvas.width = window.innerWidth;
    this.canvas.height = window.innerHeight;
    this.player = new Player();
    this.platform = new Platform(this.x, this.y, this.image);
    this.gravity = 0.7;
    this.animate();
    addEventListener('keydown', ({
      keyCode
    }) => {
      this.IS_RUNNING = true;
      switch (keyCode) {
        case 37:
          console.log('left');
          this.keys.left.pressed = true;
          break;
        case 40:
          console.log('down');
          break;
        case 38:
          console.log('up');
          this.player.velocity.y -= 10;
          break;
        case 39:
          this.IS_RUNNING = true;
          console.log('right');
          this.keys.right.pressed = true;
          break;
      }
    });
    addEventListener('keyup', ({
      keyCode
    }) => {
      this.IS_RUNNING = false;
      switch (keyCode) {
        case 37:
          console.log('left');
          this.keys.left.pressed = false;
          break;
        case 40:
          console.log('down');
          break;
        case 38:
          console.log('up');
          this.player.velocity.y -= 15;
          break;
        case 39:
          console.log('right');
          this.keys.right.pressed = false;
          break;
      }
    });
  },
  methods: {
    onClickButton() {
      this.$emit('goInfo', '1');
      this.$router.push({
        path: '/information'
      });
    },
    changeId() {
      this.id = this.days[this.dia]._id;
      this.dia++;
    },
    animate() {
      requestAnimationFrame(this.animate);
      this.c.fillStyle = 'white';
      this.c.fillRect(0, 0, this.canvas.width, this.canvas.height);
      this.scenarios.forEach(items => {
        items.draw();
      });
      this.player.update();
      if (this.keys.right.pressed && this.player.position.x < 500) {
        this.player.velocity.x = 5;
      } else if (this.keys.left.pressed && this.player.position.x > 250) {
        this.player.velocity.x = -5;
      } else {
        this.player.velocity.x = 0;
        if (this.keys.right.pressed) {
          this.scenarios.forEach(items => {
            items.position.x -= 5;
          });
        } else if (this.keys.left.pressed) {
          this.scenarios.forEach(items => {
            items.position.x += 5;
          });
        }
      }
    },
    randomScenario() {
      console.log('random');
      let num = Math.floor(Math.random() * this.customBackgrounds.length);
      console.log(num);
      return this.customBackgrounds[num];
    },
    createImage(imageSrc) {
      const image = new Image();
      image.src = imageSrc;
      console.log(image);
      return image;
    }
  }
};
