import Player from './../classes/player.js';
import Platform from './../classes/platform.js';
export default {
  name: "DayView",
  props: {
    msg: String
  },
  computed: {
    day() {
      console.log(this.$store.getters.days);
      return this.$store.getters.days.find(d => d._id == this.msg);
    }
  },
  data() {
    return {
      id: '63b3e1b9f227fb373558a9a6',
      dia: 0,
      click: false,
      player: null,
      platform: null,
      canvas: null,
      generalBackgrounds: null,
      c: null,
      keys: {
        right: {
          pressed: false
        },
        left: {
          pressed: false
        }
      },
      gravity: 0.5
    };
  },
  mounted() {
    this.canvas = this.$refs.canvas;
    this.c = this.canvas.getContext('2d');
    this.canvas.width = window.innerWidth;
    this.canvas.height = window.innerHeight;
    this.player = new Player(this.canvas, this.c, this.gravity);
    this.platform = new Platform(this.c);
    this.animate();
    addEventListener('keydown', ({
      keyCode
    }) => {
      switch (keyCode) {
        case 37:
          console.log('left');
          this.keys.left.pressed = true;
          break;
        case 40:
          console.log('down');
          break;
        case 38:
          console.log('up');
          this.player.velocity.y -= 20;
          break;
        case 39:
          console.log('right');
          this.keys.right.pressed = true;
          break;
      }
    });
    addEventListener('keyup', ({
      keyCode
    }) => {
      switch (keyCode) {
        case 37:
          console.log('left');
          this.keys.left.pressed = false;
          break;
        case 40:
          console.log('down');
          break;
        case 38:
          console.log('up');
          this.player.velocity.y -= 20;
          break;
        case 39:
          console.log('right');
          this.keys.right.pressed = false;
          break;
      }
    });
  },
  methods: {
    onClickButton() {
      this.$emit('goInfo', '1');
      this.$router.push({
        path: '/information'
      });
    },
    changeId() {
      this.id = this.days[this.dia]._id;
      this.dia++;
    },
    animate() {
      requestAnimationFrame(this.animate);
      this.c.clearRect(0, 0, this.canvas.width, this.canvas.height);
      this.player.update();
      platform.draw();
      if (this.keys.right.pressed && this.player.position.x < 400) {
        this.player.velocity.x = 5;
      } else if (this.keys.left.pressed && this.player.position.x > 100) {
        this.player.velocity.x = -5;
      } else {
        this.player.velocity.x = 0;
        if (this.keys.right.pressed) {
          platform.position.x -= 5;
        } else if (this.keys.left.pressed) {
          platform.position.x += 5;
        }
      }
    }
  }
};
