export default class Sol {
  constructor(x, y, image, c) {
    this.position = {
      x,
      y,
    };
    this.c = c;
    this.image = image;
    this.width = image.width;
    this.height = image.height;
    this.degrees = 0;
  }

  draw() {
    this.c.drawImage(this.image, this.position.x, this.position.y);
  }

  update(IS_RUNNING) {
    console.log("rotate", this.degree, this.c, this.degrees * 0.01745);
    this.c.save();
    this.c.translate(this.width / 2, this.height / 2);
    this.c.rotate(this.degrees * 0.01745);
    this.c.translate(-this.width / 2, -this.height / 2);
    if (IS_RUNNING) {
      //  this.c.translate(-500, -500);
      this.degrees++;
    }
    this.draw();
    this.c.restore();
  }
}
