import "core-js/modules/es.array.push.js";
export default {
  name: "InformationView",
  props: {
    msg: String
  },
  data() {
    return {
      saved: {
        cards: [],
        notebook: [],
        images: []
      }
    };
  },
  beforeMount() {
    if (localStorage.getItem('savedData')) {
      this.saved = JSON.parse(localStorage.getItem('savedData'));
    }
  },
  mounted() {
    console.log(this.$refs.images);
    this.$refs.images.forEach(i => {
      console.log(i, i.naturalWidth, i.naturalHeight);
      if (i.naturalWidth > i.naturalHeight) {
        console.log(i);
        i.classList.add('landscape');
      }
    });
  },
  computed: {
    notebook() {
      console.log(this.$store.getters.notebook);
      return this.$store.getters.notebook.filter(n => n.Day == this.msg);
    },
    cards() {
      console.log(this.$store.getters.cards);
      return this.$store.getters.cards.filter(d => d.map == this.msg);
    },
    images() {
      console.log(this.$store.getters.images);
      return this.$store.getters.images.filter(d => d.day == this.msg);
    },
    day() {
      console.log(this.$store.getters.days);
      return this.$store.getters.days.find(d => d._id == this.msg);
    }
  },
  methods: {
    getRatio(index) {
      console.log(this.$refs, index);
    },
    saveCard(card) {
      if (localStorage.getItem('savedData')) {
        this.saved = JSON.parse(localStorage.getItem('savedData'));
      }
      if (this.saved.cards.includes(card._id)) {
        console.log(this.saved.cards.indexOf(card._id));
        this.saved.cards.splice(this.saved.cards.indexOf(card._id), 1);
      } else {
        this.saved.cards.push(card._id);
      }
      localStorage.setItem('savedData', JSON.stringify(this.saved));
    },
    getDay(id) {
      this.selectedDay = id;
    },
    goToCard(d) {
      this.$router.push({
        path: '/card/' + d._id
      });
    },
    goToImage(d) {
      this.$router.push({
        path: '/image/' + d._id
      });
    },
    onClickButton() {
      this.$emit('goInfo', '1');
      this.$router.push({
        path: '/day'
      });
    }
  }
};
