import MenuEWorld from "@/components/MenuEWorld.vue";
import ExperienceWorld from "@/components/ExperienceWorld.vue";
export default {
  name: "DayView",
  components: {
    MenuEWorld,
    ExperienceWorld
  },
  computed: {
    days() {
      return this.$store.getters.days;
    },
    day() {
      return this.$store.getters.activeDay;
    }
  },
  methods: {},
  mounted() {
    setInterval(() => {
      this.$store.commit('setActiveDay', this.days[this.day]);
    }, 2000);
